import React, { useState } from 'react';
import { AiOutlineClose, AiOutlineMenu } from 'react-icons/ai';
import { Link } from 'react-scroll';

const Navbar = () => {
  const [nav, setNav] = useState(false);

  const handleNav = () => {
    setNav(!nav);
  };

  return (
    <div className="flex justify-between items-center h-24 max-w-[1240px] mx-auto px-4 text-white">
      <h1 className="w-full text-2xl font-bold text-[#00df9a]">devam.me</h1>
      <ul className="hidden md:flex">
        <li className="p-4">
          <Link to="home" smooth={true} duration={500}>Home</Link>
        </li>
        <li className="p-4">
          <Link to="projects" smooth={true} duration={500}>Projects</Link>
        </li>
        <li className="p-4">
          <Link to="contact" smooth={true} duration={500}>Contact</Link>
        </li>
      </ul>
      <div onClick={handleNav} className="block md:hidden">
        {nav ? <AiOutlineClose size={20}/> : <AiOutlineMenu size={20} />}
      </div>
      <ul className={nav ? 'fixed left-0 top-0 w-[60%] h-full border-r border-r-gray-900 bg-[#000300] ease-in-out duration-500' : 'ease-in-out duration-500 fixed left-[-100%]'}>
        <h1 className="w-full text-3xl font-bold text-[#00df9a] m-4">React Portfolio</h1>
        <li className="p-4 border-b border-gray-600">
          <Link to="home" smooth={true} duration={500} onClick={handleNav}>Home</Link>
        </li>
        <li className="p-4 border-b border-gray-600">
          <Link to="projects" smooth={true} duration={500} onClick={handleNav}>Projects</Link>
        </li>
        <li className="p-4">
          <Link to="contact" smooth={true} duration={500} onClick={handleNav}>Contact</Link>
        </li>
      </ul>
    </div>
  );
};

export default Navbar;
